patterns.string:
{% macro breadcrumb(items) %}
  <p class="breadcrumb">You're here: {% for item in items %}<a href="{{baseurl}}{{item.url}}" class="breadcrumb__link">{{item.title}}</a> <span class="breadcrumb__seperator">/</span> {% endfor %}&hellip;</p>
{% endmacro %}
{% macro button(title,url,modifier) %}
  <a href="{{baseurl}}{{url}}" class="button{% if modifier %} button--{{modifier}}{% endif %}">{{title}}</a>
{% endmacro %}
{% macro card(data) %}
  <div class="card">
    <h3 class="card__title">{{data.title}}</h3>
    <p class="card__content">{{data.content}}</p>
    {% if data.image %}
    <img src="{{baseurl}}{{data.img}}" class="card__image"/>
    {% endif %}
  </div>
{% endmacro %}
{% macro figure(img) %}
  {% if img.set -%}
  {% if img.gallery -%}
  <figure class="figure figure--gallery{% if img.class %} {{img.class}}{% endif %}" data-gallery{% if img.id %} id="{{img.id}}"{% endif %}>
  {% else -%}
  <figure class="figure{% if img.class %} {{img.class}}{% endif %}"{% if img.id %} id="{{img.id}}"{% endif %}>
  {%- endif %}
    <div class="figure__images figure__images--gallery">
    {% for img in img.set -%}
      <div class="figure__image" data-width="{{img.width}}" data-height="{{img.height}}" data-gallery-item >
        <img src="{{baseurl}}{{img.src}}" class="{% if img.frame %}frame--{{img.frame}}{% endif %}" />
      </div>
    {% endfor %}
    </div>
  {% else -%}
  <figure class="figure{% if img.class %} {{img.class}}{% endif %}"{% if img.id %} id="{{img.id}}"{% endif %}>
    <div class="figure__images">
      <img src="{{baseurl}}{{img.src}}" class="{% if img.frame %}frame--{{img.frame}}{% endif %}" />
    </div>
  {%- endif %}
  {% if img.caption %}
  <figcaption class="figure__caption">
    {{img.caption}}
  </figcaption>
  {% endif %}
</figure>
{% endmacro %}
{% macro grid(blocks,class) %}
  <div class="grid grid--{{class}}">
  {% for block in blocks %}
    <div class="grid__item">
      {{block}}
    </div>
  {% endfor %}
  </div>
{% endmacro %}
{% macro icon(name) %}
  <svg class="icon icon--{{ name }}"><use xlink:href="{{baseurl}}/images/site-graphics.svg#{{ name }}"></use></svg>
{% endmacro %}
{% macro meta(data) %}
  <table class="meta">
    <tbody class="meta__body">
    {% for key, vaule in data %}
      <tr>
        <th class="meta__key">
          {{key | capitalize}}
        </th>
        <td class="meta__value">
          {{vaule}}
        </td>
      </tr>
    {% endfor %}
    </tbody>
  </table>
{% endmacro %}
{% macro pagination(prev,next,base) %}
  <nav class="pagination">
    {% if prev %}
      <p class="pagination__prev">&larr;&nbsp;Previous <a href="{{baseurl}}/{{base}}/{{prev.url}}">{{prev.title}}</a></p>
    {% endif %}
    {% if next %}
      <p class="pagination__next">Next <a href="{{baseurl}}/{{base}}/{{next.url}}">{{next.title}}</a>&nbsp;&rarr;</p>
    {% endif %}
  </nav>
{% endmacro %}
{% macro postinfo(date,tags,base) %}
<div class="postinfo">
  <p class="postinfo__item postinfo__item--date">{{date}}</p>
  {% if tags %}
  <p class="postinfo__item postinfo__item--tags">{% for tag in tags %}#<a href="{{baseurl}}/{{base}}/tags/{{tag|lower}}" class="postinfo__link">{{tag}}</a> {% endfor %}</p>
  {% endif %}
</div>
{% endmacro %}
{% from "_patterns/button/button.html" import "button" %}

{% macro teaser(title,content,url,cta,level=2,modifier) %}
  <div class="teaser{% if modifier %} teaser--{{modifier}}{% endif %}">
    <h{{level}} class="teaser__title teaser__title--{{level}}">{% if url %}<a href="{{baseurl}}{{url}}" class="teaser__link">{{title}}</a>{% else %}{{title}}{% endif %}</h{{level}}>
    <p class="teaser__content">{{content}}</p>
    {% if url %}{{button(cta,url,modifier=modifier)}}{% endif %}
  </div>
{% endmacro %}
{% from "_patterns/icon/icon.html" import "icon" %}

{% macro timemarker(label,isodate,modifier) %}
  <div class="timemarker{% if modifier %} timemarker--{{modifier}}{% endif %}">
    {{ icon('coin') }}
    <time{% if isodate %} datetime="{{isodate}}"{% endif %} class="timemarker__label">{{label}}</time>
  </div>
{% endmacro %}
{% macro title(title,date,isodate,level=1) %}
  <div class="title">
    <h{{level}} class="title__text title__text--{{level}}">{{title}}</h{{level}}>
    {% if date %}<time{% if isodate %} datetime="{{isodate}}"{% endif %} class="title__date">{{date}}</time>{% endif %}
  </div>
{% endmacro %}
